// Iterate through the sizes and create a media template
export const media: any = Object.keys(sizes).reduce((acc, label) => {
  acc[label] = (...args: string[]) => `
    @media (max-width: ${sizes[label]}px) {${args}
  }
  `
  return acc
}, {})

